(187, 'aioseop_options', 'a:85:{s:16:"aiosp_home_title";s:25:"Ashglow Official Web Site";s:22:"aiosp_home_description";s:455:"福岡県北九州市を拠点に活動をしているギターロックバンドAshglow（アッシュグロウ）2009年に結成、数回のメンバーチェンジを経て、現メンバー体制となる。楽曲は主にツインギター・ベース・ドラムというバンドの基本構成を軸に、楽曲により鍵盤やシンセサイザー・ストリング等の音色を取り入れ、多彩な音色を用いて表現している。";s:20:"aiosp_togglekeywords";s:1:"0";s:19:"aiosp_home_keywords";s:0:"";s:26:"aiosp_use_static_home_info";s:1:"0";s:9:"aiosp_can";s:2:"on";s:30:"aiosp_no_paged_canonical_links";s:0:"";s:31:"aiosp_customize_canonical_links";s:0:"";s:20:"aiosp_rewrite_titles";s:1:"1";s:20:"aiosp_force_rewrites";s:1:"1";s:24:"aiosp_use_original_title";s:1:"0";s:16:"aiosp_cap_titles";s:2:"on";s:28:"aiosp_home_page_title_format";s:12:"%page_title%";s:23:"aiosp_page_title_format";s:27:"%page_title% | %blog_title%";s:23:"aiosp_post_title_format";s:27:"%post_title% | %blog_title%";s:27:"aiosp_category_title_format";s:31:"%category_title% | %blog_title%";s:26:"aiosp_archive_title_format";s:30:"%archive_title% | %blog_title%";s:23:"aiosp_date_title_format";s:21:"%date% | %blog_title%";s:25:"aiosp_author_title_format";s:23:"%author% | %blog_title%";s:22:"aiosp_tag_title_format";s:20:"%tag% | %blog_title%";s:25:"aiosp_search_title_format";s:23:"%search% | %blog_title%";s:24:"aiosp_description_format";s:13:"%description%";s:22:"aiosp_404_title_format";s:33:"Nothing found for %request_words%";s:18:"aiosp_paged_format";s:14:" - Part %page%";s:17:"aiosp_enablecpost";s:2:"on";s:17:"aiosp_cpostactive";a:2:{i:0;s:4:"post";i:1;s:4:"page";}s:19:"aiosp_cpostadvanced";s:1:"0";s:18:"aiosp_cpostnoindex";s:0:"";s:19:"aiosp_cpostnofollow";s:0:"";s:17:"aiosp_cposttitles";s:0:"";s:21:"aiosp_posttypecolumns";a:2:{i:0;s:4:"post";i:1;s:4:"page";}s:19:"aiosp_google_verify";s:0:"";s:17:"aiosp_bing_verify";s:0:"";s:22:"aiosp_pinterest_verify";s:0:"";s:22:"aiosp_google_publisher";s:0:"";s:28:"aiosp_google_disable_profile";s:0:"";s:29:"aiosp_google_sitelinks_search";s:0:"";s:26:"aiosp_google_set_site_name";s:0:"";s:30:"aiosp_google_specify