INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 2, '2017-09-17 17:16:48', '2017-09-17 08:16:48', 'WordPress へようこそ。これは最初の投稿です。編集もしくは削除してブログを始めてください !', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world__trashed', '', '', '2017-09-18 17:47:22', '2017-09-18 08:47:22', '', 0, 'http://ashglow.com/?p=1', 0, 'post', '', 1),
(2, 2, '2017-09-17 17:16:48', '2017-09-17 08:16:48', 'これはサンプルページです。同じ位置に固定され、(多くのテーマでは) サイトナビゲーションメニューに含まれるため、ブログ投稿とは異なります。サイト訪問者に対して自分のことを説明する自己紹介ページを作成するのが一般的です。たとえば以下のようなものです。\n\n<blockquote>はじめまして。昼間はバイク便のメッセンジャーとして働いていますが、俳優志望でもあります。これは僕のサイトです。ロサンゼルスに住み、ジャックという名前のかわいい犬を飼っています。好きなものはピニャコラーダ、そして通り雨に濡れること。</blockquote>\n\nまたは、このようなものです。\n\n<blockquote>XYZ 小道具株式会社は1971年の創立以来、高品質の小道具を皆様にご提供させていただいています。ゴッサム・シティに所在する当社では2,000名以上の社員が働いており、様々な形で地域のコミュニティへ貢献しています。</blockquote>\n\n新しく WordPress ユーザーになった方は、<a href="http://ashglow.com/wp-admin/">ダッシュボード</a>へ行ってこのページを削除し、独自のコンテンツを含む新しいページを作成してください。それでは、お楽しみください !', 'サンプルページ', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-09-17 17:16:48', '2017-09-17 08:16:48', '', 0, 'http://ashglow.com/?page_id=2', 0, 'page', '', 0),
(3, 2, '2017-09-17 17:16:59', '0000-00-00 00:00:00', '', '自動下書き', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-09-17 17:16:59', '0000-00-00 00:00:00', '', 0, 'http://ashglow.com/?p=3', 0, 'post', '', 0),
(4, 2, '2017-09-18 02:51:18', '2017-09-17 17:51:18', '{\n    "old_sidebars_widgets_data": {\n        "value": {\n            "wp_inactive_widgets": [],\n            "sidebar-1": [\n                "search-2",\n                "recent-posts-2",\n                "recent-comments-2",\n                "archives-2",\n                "categories-2",\n                "meta-2"\n            ],\n            "sidebar-2": [],\n            "sidebar-3": []\n        },\n        "type": "global_variable",\n        "user_id": 1\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'd82dda1b-1acd-4a83-b517-61e3b2bdcdf0', '', '', '2017-09-18 02:51:18', '2017-09-17 17:51:18', '', 0, 'http://ashglow.com/%e6%9c%aa%e5%88%86%e9%a1%9e/d82dda1b-1acd-4a83-b517-61e3b2bdcdf0', 0, 'customize_changeset', '', 0),
(5, 2, '2017-09-18 03:18:13', '2017-09-17 18:18:13', '<label> Your Name (required)\n    [text* your-name] </label>\n\n<label> Your Email (required)\n    [email* your-email] </label>\n\n<label> Subject\n    [text your-subject] </label>\n\n<label> Your Message\n    [textarea your-message] </label>\n\n[submit "Send"]\nAshglow "[your-subject]"\n[your-name] <wordpress@ashglow.dev>\nFrom: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on Ashglow (http://ashglow.com)\nashglow.information@gmail.com\nReply-To: [your-email]\n\n0\n0\n\nAshglow "[your-subject]"\nAshglow <wordpress@ashglow.dev>\nMessage Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on Ashglow (http://ashglow.com)\n[your-email]\nReply-To: ashglow.information@gmail.com\n\n0\n0\nThank you for your message. It has been sent.\nThere was an error trying to send your message. Please try again later.\nOne or more fields have an error. Please check and try again.\nThere was an error trying to send your message. Please try again later.\nYou must accept the terms and conditions before sending your message.\nThe field is required.\nThe field is too long.\nThe field is too short.', 'Contact form 1', '', 'publish', 'closed', 'closed', '', 'contact-form-1', '', '', '2017-09-18 03:18:13', '2017-09-17 18:18:13', '', 0, 'http://ashglow.com/?post_type=wpcf7_contact_form&p=5', 0, 'wpcf7_contact_form', '', 0),
